-- ================================
-- Validation Queries
-- ================================

DO $$
DECLARE
  driver_count INT;
  session_count INT;
  lap_count INT;
BEGIN
  -- Count demo drivers
  SELECT COUNT(*) INTO driver_count
  FROM drivers
  WHERE email LIKE '%@trackapp.demo';

  -- Count demo sessions
  SELECT COUNT(*) INTO session_count
  FROM sessions s
  JOIN drivers d ON d.id = s.driver_id
  WHERE d.email LIKE '%@trackapp.demo';

  -- Count demo laps
  SELECT COUNT(*) INTO lap_count
  FROM laps l
  JOIN sessions s ON s.id = l.session_id
  JOIN drivers d ON d.id = s.driver_id
  WHERE d.email LIKE '%@trackapp.demo';

  -- Report
  RAISE NOTICE '========================================';
  RAISE NOTICE 'Demo Data Seeding Complete!';
  RAISE NOTICE '========================================';
  RAISE NOTICE 'Drivers with @trackapp.demo emails: %', driver_count;
  RAISE NOTICE 'Sessions for demo drivers: %', session_count;
  RAISE NOTICE 'Laps for demo drivers: %', lap_count;
  RAISE NOTICE '========================================';
END $$;

-- Summary query
SELECT
  d.name as driver,
  dp.experience_level as experience,
  COUNT(DISTINCT s.id) as sessions,
  COUNT(l.id) as total_laps,
  MIN(s.best_lap_ms) as best_lap_ms,
  MIN(s.date)::date as first_session,
  MAX(s.date)::date as last_session
FROM drivers d
LEFT JOIN driver_profiles dp ON d.id = dp.driver_id
LEFT JOIN sessions s ON d.id = s.driver_id
LEFT JOIN laps l ON s.id = l.session_id
WHERE d.email LIKE '%@trackapp.demo'
GROUP BY d.id, d.name, dp.experience_level
ORDER BY d.name;

COMMIT;
